<?php/** * class Rubric * */class Rubric{    /** Aggregations: */    /** Compositions: */     /*** Attributes: ***/    /**     *     * @access protected     */    protected $_id;    /**     *     * @access protected     */    protected $_name;    /**     *     * @access protected     */    protected $_parent;    /**     *     * @access protected     */    protected $_isRoot;    /**     *     *     * @param int id     * @param string name     * @param int parent     * @return     * @access public     */    public function __construct( $id,  $name,  $parent ) {        $this->_id = $id;        $this->_name = $name;        $this->_parent = $parent;    } // end of member function __construct    /**     *     *     * @return int     * @access public     */    public function getId( ) {        return $this->_id;    } // end of member function getId    /**     *     *     * @return string     * @access public     */    public function getName( ) {        return $this->_name;    } // end of member function getName    /**     *     *     * @return int     * @access public     */    public function getParent( ) {        return $this->_parent;    } // end of member function getParent    /**     *     *     * @return bool     * @access public     */    public function getIsRoot( ) {        return $this->_isRoot;    } // end of member function getIsRoot    /**     *     *     * @param int value     * @return     * @access public     */    public function setId( $value ) {        $this->_id = $value;    } // end of member function setId    /**     *     *     * @param string value     * @return     * @access public     */    public function setName( $value ) {        $this->_name = $value;    } // end of member function setName    /**     *     *     * @param int value     * @return int     * @access public     */    public function setParent( $value ) {        $this->_parent = $value;    } // end of member function setParent    /**     *     *     * @param bool value     * @return     * @access public     */    public function setIsRoot( $value ) {        $this->_isRoot = $value;    } // end of member function setIsRoot} // end of Rubric?>