<?php/** * class ProductCatalog *  */class ProductCatalog {    /** Aggregations: */    /** Compositions: */    /*     * * Attributes: ** */    private $_db;    public function __construct() {        $this->_db = simo_db::getInstance();    }    public function getAllRubric($parent) {        try {            $result = $this->_db->query('SELECT * FROM product_rubric WHERE parent_id=' . $parent, simo_db::QUERY_MOD_ASSOC);            if (isset($result[0])) {                $rubricArray = array();                foreach ($result as $value) {                    $rubricArray[] = Rubric::getInstanceByArray($value);                }                return $rubricArray;            }                    } catch (Exception $e) {            simo_exception::registrMsg($e, $this->_debug);            return false;        }    }}// end of ProductCatalog?>