INSERT INTO `product` (`id`, `product_rubric_id`, `title`, `img`, `short_text`, `full_text`, `price`, `is_spec`) VALUES
(1, 3, 'Апельсин', 'img_30-06-2011-21-19-07.jpg', 'Просто апельсин', 'Детальная информация об апельсинах', '100.00', 0),
(3, 3, 'Вишня', 'img_30-06-2011-21-11-19.jpg', 'Просто вишня', 'Просто сладкая вишня', '250.00', 0),
(6, 14, 'Крем для век от морщин, 50г', 'img_06-05-2012-11-46-22.jpg', 'Увлажнение кожи и разглаживание морщинок ', 'Крем для век от морщин\r\n50 г\r\n\r\nУвлажнение кожи и разглаживание морщинок \r\n\r\nОписание\r\nМинеральные компоненты, входящие в состав крема, действуют непосредственно на коже, сохраняя все свои полезные свойства, активные вещества глубоко проникают и полностью усваиваются кожей. Гелевая текстура крема мгновенно освежает и делает кожу век упругой и подтянутой.\r\n\r\nКогда вам это нужно\r\nРекомендуется с 35 лет\r\nКод: 10306', '261.00', 0),
(7, 14, 'Пилинг для лица на основе плаценты, 80г', 'img_06-05-2012-11-52-09.jpg', 'Глубокое очищение', 'Пилинг для лица на основе плаценты\r\n80 г\r\n\r\nГлубокое очищение\r\n\r\nОписание\r\nЭффективно убирает слой отмерших клеток, улучшает регенерацию клеток кожи, усиливает метаболизм. При длительном использовании удаляет последствия ожогов и царапин. Питает и увлажняет. Возвращает коже естественную белизну и эластичность.\r\nКод: 10301', '237.00', 0),
(8, 14, 'Выравнивающий крем, 50г', 'img_06-05-2012-12-05-20.jpg', 'Восстанавливающий уход\r\n', 'Выравнивающий крем, 50г\r\n\r\nВосстанавливающий уход\r\n\r\nОписание\r\nКрем эффективно удаляет морщины, темные, желто-коричневые пигментные пятна, а также пятна, появляющиеся во время беременности, угри (акне) и шрамы, возникающие после удаления угрей. Эффективен в любом возрасте, подходит для любого типа кожи.\r\n\r\nКод: 10302\r\n', '237.00', 0),
(9, 14, 'Восстанавливающий крем для лица от морщин, 50г', 'img_06-05-2012-12-09-07.jpg', 'Омолаживающий, восстанавливающий уход', '\r\nВосстанавливающий крем для лица от морщин\r\n50 г\r\n\r\nОмолаживающий, восстанавливающий уход\r\n\r\nОписание\r\nСодержит множество питательных компонентов, а также экстракт плаценты овцы, витамины Е и С, фибрин. Смягчает и питает, улучшает обмен веществ и циркуляцию крови, возвращая коже жизненную силу и эластичность. Препятствует процессам старения кожи и появлению морщин. Эффективен в любом возрасте, подходит для любого типа кожи.\r\n\r\nКод: 10303', '237.00', 0),
(10, 14, 'Увлажняющий крем с питательной основой, 50г', 'img_06-05-2012-12-12-17.jpg', 'Увлажнение, утренний уход за кожей лица', '\r\nУвлажняющий крем с питательной основой\r\n50 г\r\n\r\nУвлажнение, утренний уход за кожей лица\r\n\r\nОписание\r\nГлубоко проникает в клетки кожи, увлажняя и питая ее; предохраняет от UF-воздействия. Возвращает коже жизненную силу и эластичность. Препятствует процессам старения кожи и появлению морщин. Эффективен для сухого и комбинированного типов кожи.\r\n\r\nКод: 10304', '237.00', 0),
(11, 14, 'Тональный крем для лица, 50г', 'img_06-05-2012-12-15-43.jpg', 'Для любого типа и тона кожи', '\r\nТональный крем для лица\r\n50 г\r\n\r\nДля любого типа и тона кожи\r\n\r\nОписание\r\nСодержит биологические компоненты на основе морских водорослей, плаценты, природной аминокислоты и высокоэффективных увлажняющих ингредиентов. Активизирует работу клеток, укрепляет мембраны клеток кожи, способствует выведению токсинов. Прекрасно защищает кожу от УФ-воздействия в течение всего дня. Придает лицу бархатистость и нежный естественный оттенок. Может использоваться в любом возрасте, подходит для любого типа кожи.\r\n\r\nКод: 10305', '237.00', 0),
(12, 5, 'Крем-гель для умывания ', 'img_06-05-2012-12-33-13.jpg', 'Ежедневное очищение, для всех типов кожи', 'Крем-гель для умывания (с экстрактом алоэ), 100 г\r\n\r\nОписание: нежный гипоаллергенный крем-гель при соприкосновении с кожей легко превращается в ароматную пенку, которая глубоко проникает в поры. Деликатно и в то же время эффективно удаляет с поверхности кожи любые загрязнения и макияж, хорошо очищает поры, избавляет от жирного блеска. Экстракт алоэ вера, входящий в состав геля, сохраняет естественную увлажненность кожи, снимает воспаления и раздражения. \r\n\r\nРезультат: естественное ощущение свежести и чистоты на коже лица.\r\n\r\nКод: 12001\r\n\r\n', '286.00', 1),
(13, 5, 'Балансирующий тоник для лица "Алоэ", 100мл', 'img_06-05-2012-12-44-32.jpg', 'Ежедневное очищение, тонизирование, для всех типов кожи', 'Балансирующий тоник для лица «203040…» (ежедневное очищение, тонизирование, для всех типов кожи)\r\n\r\nОписание\r\nОбогащенное натуральным экстрактом алоэ, средство нежно тонизирует кожу, освежает, снимает раздражения и покраснения. Витамины и минералы питают кожу. Гиалуроновая кислота активно увлажняет и разглаживает кожу. \r\n\r\nКод: 12002', '317.00', 0),
(14, 5, 'Крем-пенка для умывания (антиакне) «Алоэ», 100г', 'img_06-05-2012-12-49-21.jpg', 'Эффективное очищение кожи ', '\r\nКрем-пенка для умывания (антиакне) «Алоэ»\r\n100 г\r\n\r\nЭффективное очищение кожи \r\n\r\nОписание\r\nМягкая пенка с экстрактом алоэ против жирного блеска, черных точек и несовершенств кожи. Эффективно и глубоко очищает кожу, сужает поры, нормализует работу сальных желез. Натуральный экстракт алоэ хорошо увлажняет кожу и предотвращает эффект стягивания после умывания. Ваша кожа сияет чистотой и здоровьем.\r\n\r\nКогда вам это нужно\r\nПодходит для всех типов кожи.\r\n\r\nКод: 12025', '296.00', 0),
(15, 5, 'Тонизирующий гидрогель для лица "Алоэ", 100мл', 'img_06-05-2012-12-52-19.jpg', 'Длительное увлажнение ', '\r\nТонизирующий гидрогель для лица\r\n100 мл\r\n\r\nДлительное увлажнение \r\n\r\nОписание\r\nНежно очищает и освежает кожу, является идеальным завершением процедуры очищения. Нормализует гидролипидный баланс, успокаивает, увлажняет и укрепляет структуру кожи, делая ее более гладкой и эластичной.\r\n\r\nКогда вам это нужно\r\nПодходит для всех типов кожи.\r\n\r\nКод: 12026', '300.00', 0),
(16, 5, 'Очищающее молочко для умывания, 100г', 'img_06-05-2012-12-57-20.jpg', 'Ежедневное очищение, для чувствительной кожи', '\r\nОчищающее молочко для умывания\r\n100 г\r\n\r\nОчищающее молочко для умывания «203040…» (ежедневное очищение, для чувствительной кожи)\r\n\r\nОписание\r\nОчень бережно очищает кожу, делает ее мягкой, как у ребенка, нормализует рН- и гидробаланс. Для кожи, требующей особого внимания и деликатного ухода: чувствительной, поврежденной, усталой, склонной к стрессу.\r\n\r\nКод: 12003', '295.00', 0),
(17, 5, 'Увлажняющий тоник для лица, 100мл', 'img_06-05-2012-13-00-23.jpg', 'Ежедневное очищение, тонизирование, для чувствительной кожи', '\r\nУвлажняющий тоник для лица\r\n100 мл\r\n\r\nУвлажняющий тоник для лица «203040…» (ежедневное очищение, тонизирование, для чувствительной кожи)\r\n\r\nОписание\r\nБлагодаря экстракту гамамелиса и пантенолу эффективно приводит кожу в тонус, нормализует уровень ее рН, снимает раздражения, выравнивает текстуру. Гиалуроновая кислота уменьшает испарение влаги через роговой слой, восстанавливает гидробаланс кожи.\r\n\r\nКод: 12004', '319.00', 0),
(18, 5, 'Увлажняющий дневной крем с SPF-12, 50г', 'img_06-05-2012-13-12-56.jpg', 'Увлажнение, защита от УФ-лучей, утренний уход за кожей лица, для чувствительной кожи', '\r\nУвлажняющий дневной крем с SPF-12\r\n50 г\r\n\r\nУвлажняющий дневной крем с SPF-12 «203040…» (увлажнение, защита от УФ-лучей, утренний уход за кожей лица, для чувствительной кожи)\r\n\r\nОписание\r\nНасыщенный многофункциональный крем обеспечивает оптимальный уровень увлажнения кожи в течение всего дня, защищает от вредного воздействия УФ-лучей. Экстракт миндаля, усиленный гиалуроновой кислотой, восстанавливает упругость кожи. Растворимый коллаген способствует регенерации кожи.\r\n\r\nКод: 12017', '361.00', 0),
(19, 5, 'Пилинг-гель для лица (активный), 80г', 'img_06-05-2012-13-27-14.jpg', 'Активный, для всех типов кожи', '\r\nПилинг-гель для лица (активный)\r\n80 г\r\n\r\nПилинг-гель для лица «203040…» (активный, для всех типов кожи)\r\n\r\nОписание\r\nОбеспечивает активную эксфолиацию отмерших клеток рогового слоя, уменьшает морщины, сужает поры. Содержит экстракт морских водорослей, активизирующий внутриклеточные процессы. Активно и глубоко очищает и омолаживает кожу, осветляет пигментные пятна.\r\n\r\nКод: 12008', '386.00', 0),
(20, 5, 'Пилинг-гель для лица (деликатный), 100г', 'img_06-05-2012-13-30-05.jpg', 'Для всех типов кожи, в том числе чувствительной', '\r\nПилинг-гель для лица (деликатный)\r\n100 г\r\n\r\nПилинг-гель для лица «203040…» (деликатный, для всех типов кожи, в том числе чувствительной)\r\n\r\nОписание\r\nПилинг выравнивает структуру кожи, разглаживает морщины, интенсивно увлажняет, обогащает клетки кожи кислородом, ускоряет их обновление. Фруктовые кислоты, входящие в его состав, деликатно размягчают и снимают ороговевший слой кожи.\r\n\r\nКод: 12009', '297.00', 0),
(21, 5, 'Активная эссенция от морщин, 30г', 'img_06-05-2012-13-34-38.jpg', 'Интенсивная коррекция морщин, вечерний уход за кожей лица, для всех типов кожи с 40 лет', '\r\nАктивная эссенция от морщин\r\n30г\r\n\r\nАктивная эссенция от морщин «203040…» (интенсивная коррекция морщин, вечерний уход за кожей лица, для всех типов кожи с 40 лет)\r\n\r\nОписание\r\nВысококонцентрированная формула эссенции с активным коллагеном и гиалуроновой кислотой интенсивно корректирует возрастные изменения кожи, оказывает выраженное омолаживающее воздействие, восстанавливает защитную и влагоудерживающую способности кожи.\r\n\r\nКод: 12011', '399.00', 0),
(22, 5, 'Крем-гель с биозолотом для кожи вокруг глаз, 30г', 'img_06-05-2012-13-38-20.jpg', 'Лифтинг-эффект, с 30 лет', '\r\nКрем-гель с биозолотом для кожи вокруг глаз (лифтинг-эффект)\r\n30 г\r\n\r\nКрем-гель с биозолотом для кожи вокруг глаз «203040…» (лифтинг-эффект, с 30 лет)\r\n\r\nОписание\r\nПрогрессивная формула крема включает интенсивный лифтинговый комплекс на основе биозолота и морского коллагена. Крем-гель видимо уменьшает мешочки и темные круги под глазами, значительно снижает отечность, корректирует морщины.\r\n\r\nКод: 12012', '433.00', 0),
(23, 5, 'Крем-гель с биозолотом для кожи вокруг глаз, 30г', 'img_06-05-2012-13-40-54.jpg', 'от морщин, с 30 лет', '\r\nКрем-гель с биозолотом для кожи вокруг глаз (от морщин)\r\n30 г\r\n\r\nКрем-гель с биозолотом для кожи вокруг глаз «203040…» (от морщин, с 30 лет)\r\n\r\nОписание\r\nЭксклюзивная формула крема основана на высоких проводниковых свойствах биозолота. В сочетании с гиалуроновой кислотой средство приобретает редкие восстанавливающие свойства. Эффективно уменьшает морщины, восстанавливает упругость и эластичность кожи.\r\n\r\nКод: 12013', '433.00', 0),
(24, 5, 'Гель для кожи вокруг глаз от морщин, 30г', 'img_06-05-2012-13-43-29.jpg', 'омоложение, с 40 лет', '\r\nГель для кожи вокруг глаз от морщин\r\n30 г\r\n\r\nГель для кожи вокруг глаз от морщин «203040…» (омоложение, с 40 лет)\r\n\r\nОписание\r\nУльтранасыщенный состав геля на основе морского коллагена, гиалуроновой кислоты, пшеничного протеина и витаминного комплекса оказывает активное возрождающее действие на кожу век. Интенсивно стимулирует регенерацию, ускоряет синтез коллагена. Обладает выраженным антиоксидантным и противоотечным действием.\r\n\r\nКод: 12014', '572.00', 0),
(25, 5, 'Балансирующий дневной крем "Алоэ", 50г', 'img_06-05-2012-13-48-10.jpg', 'увлажнение, утренний уход за кожей лица, для всех типов кожи', '\r\nБалансирующий дневной крем\r\n50 г\r\n\r\nБалансирующий дневной крем «203040…» (увлажнение, утренний уход за кожей лица, для всех типов кожи)\r\n\r\nОписание\r\nОбеспечивает эффективное увлажнение, нормализует работу сальных желез. Алоэ вера активизирует обменные процессы в коже, повышает ее защитные свойства, наделяет крем бактерицидным и тонизирующим действиями, способствует быстрому заживлению поврежденной кожи.\r\n\r\nКод: 12016', '346.00', 0),
(26, 5, 'Увлажняющий крем для лица 24-часового действия, 50г', 'img_06-05-2012-13-53-41.jpg', 'увлажнение, для всех типов кожи с 20 лет', '\r\nУвлажняющий крем для лица 24-часового действия\r\n50 г\r\n\r\nУвлажняющий крем для лица 24-часового действия «203040…» (увлажнение, для всех типов кожи с 20 лет)\r\n\r\nОписание\r\nОбеспечивает оптимальный уровень увлажненности кожи на 24 часа, выравнивает микрорельеф кожи, снижает шелушение и раздражение кожи. Уникальный комплекс из токоферола и растворимого коллагена замедляет процессы старения. Защищает от агрессивного воздействия атмосферных явлений.\r\n\r\nКод: 12018', '455.00', 0),
(27, 5, 'Крем ночной восстанавливающий, 50г', 'img_06-05-2012-13-58-19.jpg', 'питание, вечерний уход за кожей лица, для всех типов кожи с 30 лет', '\r\nКрем ночной восстанавливающий\r\n50 г\r\n\r\nКрем ночной восстанавливающий «203040…» (питание, вечерний уход за кожей лица, для всех типов кожи с 30 лет)\r\n\r\nОписание\r\nНасыщенный крем с экстрактами водорослей и алоэ, гиалуроновой кислотой и керамидами интенсивно питает, увлажняет и омолаживает кожу. Активные компоненты крема восстанавливают целостность структуры дермы и межклеточные связи, улучшают цвет лица, снимают напряжение и стресс кожи.\r\n\r\nКод: 12019', '348.00', 0),
(28, 5, 'Увлажняющий крем с протеинами шелка, 50г', 'img_06-05-2012-14-07-25.jpg', 'Интенсивное увлажнение, с 30 лет', '\r\nУвлажняющий крем с протеинами шелка\r\n50 г\r\n\r\nИнтенсивное увлажнение, с 30 лет\r\n\r\nОписание\r\nИнтенсивно увлажняет, разглаживает мелкие морщинки, улучшает рельеф кожи, делает ее более упругой и гладкой. Аминокислоты шелка активизируют регенерацию кожного покрова, защищают кожу от воздействия агрессивных факторов внешней среды. Результат: поврежденные клетки восстанавливаются, а глубоко увлажненная кожа сияет здоровьем и красотой.\r\n\r\nКогда вам это нужно\r\nРекомендован как ежедневный увлажняющий крем для всех типов кожи и как интенсивный восстанавливающий крем для кожи с признаками дегидратации.\r\n\r\nКод: 12022', '450.00', 0),
(29, 5, 'Увлажняющая маска c протеинами шелка, 38г', 'img_06-05-2012-14-11-42.jpg', 'упругость и сияние кожи, с 30 лет', '\r\nУвлажняющая маска c протеинами шелка\r\n38 г\r\n\r\nУвлажняющая маска c протеинами шелка «203040…» (упругость и сияние, с 30 лет)\r\n\r\nОписание\r\nПротеины шелка интенсивно увлажняют и разглаживают кожу, уменьшают видимость возрастных изменений, питают, смягчают, поддерживают эластичность, улучшают цвет лица, дарят необычайный комфорт.\r\nМаска является достойной альтернативой увлажняющим салонным процедурам класса люкс. При регулярном использовании после 30 лет оказывает стойкое омолаживающее действие. Может применяться в любом возрасте как средство экстренной помощи при обезвоживании кожи.\r\nОснова-муляж маски состоит из тончайшего плетения шелковых нитей. Стабильность фиксации и эстетичный внешний вид позволяют применять маску в любое удобное для вас время, не отвлекаясь от домашних дел.\r\n\r\nКод: 12023', '178.00', 0),
(30, 5, 'Крем-гель с биозолотом для кожи вокруг глаз, 30г', 'img_06-05-2012-14-14-46.jpg', 'ревитализация, с 30 лет', '\r\nКрем-гель с биозолотом для кожи вокруг глаз (ревитализация)\r\n30 г\r\n\r\nКрем-гель с биозолотом для кожи вокруг глаз «203040…» (ревитализация, с 30 лет)\r\n\r\nОписание\r\nКрем-гель с биозолотом и пептидом гороха обеспечивает глубокое питание кожи вокруг глаз, заметно восстанавливает ее структуру и активизирует клеточные процессы. Крем уменьшает количество существующих морщинок и их глубину, повышает упругость и эластичность кожи, увлажняет, питает и защищает ее от вредных внешних воздействий.\r\nДля лучшего эффекта используйте утром и вечером.\r\n\r\nКод: 12021', '433.00', 0),
(31, 5, 'Крем-пенка для умывания (антиакне) "Олива", 100г', 'img_06-05-2012-14-18-53.jpg', 'Мягкое очищение, антистресс, для проблемной кожи', '\r\nКрем-пенка для умывания (антиакне) «Олива»\r\n100 г\r\n\r\nМягкое очищение \r\n\r\nОписание\r\nМягкое средство прекрасно пенится и очищает кожу от загрязнений, остатков косметических средств. Содержит натуральный экстракт оливы, известный своими смягчающими свойствами, нормализует гидробаланс кожи, регулирует деятельность сальных желез, предупреждая появление жирного блеска и раздражения, увлажняет и успокаивает кожу. \r\n\r\nКогда вам это нужно\r\nДля проблемной кожи и находящейся в состоянии стресса\r\n\r\nКод: 12024', '350.00', 0),
(32, 5, 'Глубоко очищающий лосьон для лица (антиакне) "Олива", 100мл', 'img_06-05-2012-14-22-01.jpg', 'Бережное и глубокое очищение воспаленной кожи', '\r\nГлубоко очищающий лосьон для лица (антиакне) «Олива»\r\n\r\n100 мл\r\n\r\nБережное и глубокое очищение воспаленной кожи \r\n\r\nОписание\r\nЛосьон предназначен для проблемной кожи, склонной к образованию угрей и прыщей. Бережно и глубоко очищает воспаленную кожу, сужает поры, регулирует работу сальных желез. Устраняет покраснения и шелушение, оказывает восстанавливающее действие, одновременно успокаивая и смягчая кожу.\r\n\r\nКогда вам это нужно\r\nРекомендован для проблемной кожи и находящейся в состоянии стресса.\r\n\r\nКод: 12027', '376.00', 0),
